import React, { useState } from 'react';
import './styles/BorrowersList.css'; // Create a CSS file for styling

const BorrowersList = ({ onGoBack }) => {
    const [bookId, setBookId] = useState('');
    const [students, setStudents] = useState([]);

    const handleSubmit = (e) => {
        e.preventDefault();
        fetch(`/loans/book/${bookId}`)
            .then(res => res.json())
            .then(data => setStudents(data))
            .catch(err => console.error(err));
    };

    return (
        <div className="borrowers-list-container">
            <h2 className="title">Étudiants ayant emprunté un Livre</h2>
            <form onSubmit={handleSubmit} className="form">
                <input
                    type="text"
                    placeholder="ID du Livre"
                    value={bookId}
                    onChange={e => setBookId(e.target.value)}
                    className="input"
                />
                <button type="submit" className="list-button">Lister</button>
            </form>
            <ul className="student-list">
                {students.length > 0 ? (
                    students.map(student => (
                        <li key={student.id} className="student-item">{student.name}</li>
                    ))
                ) : (
                    <li className="no-students">Aucun étudiant n'a emprunté ce livre.</li>
                )}
            </ul>
            <button onClick={onGoBack} className="back-button">Retour</button>
        </div>
    );
};

export default BorrowersList;
