import React, { useState } from 'react';

const AddBook = () => {
    const [title, setTitle] = useState('');
    const [author, setAuthor] = useState('');
    const [category, setCategory] = useState('');
    const [message, setMessage] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();

        // Utilisation de fetch pour envoyer les données au backend
        fetch('http://localhost:5000/books', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ title, author, category })
        })
        .then(response => {
            if (!response.ok) {
                throw new Error('Erreur lors de l\'ajout du livre');
            }
            return response.json();
        })
        .then(data => {
            setMessage('Livre ajouté avec succès');
            setTitle('');
            setAuthor('');
            setCategory('');
        })
        .catch(error => {
            console.error('Erreur:', error);
            setMessage('Une erreur est survenue lors de l\'ajout du livre');
        });
    };

    return (
        <div>
            <h2>Ajouter un Livre</h2>
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    placeholder="Titre du livre"
                    value={title}
                    onChange={e => setTitle(e.target.value)}
                />
                <input
                    type="text"
                    placeholder="Auteur du livre"
                    value={author}
                    onChange={e => setAuthor(e.target.value)}
                />
                <input
                    type="text"
                    placeholder="Catégorie du livre"
                    value={category}
                    onChange={e => setCategory(e.target.value)}
                />
                <button type="submit">Ajouter</button>
            </form>
            {message && <p>{message}</p>}
        </div>
    );
};

export default AddBook;
