import React, { useState } from 'react';

const ReturnBook = () => {
    const [bookId, setBookId] = useState('');
    const [studentId, setStudentId] = useState('');
    const [message, setMessage] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        fetch('/returns', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ book_id: bookId, student_id: studentId })
        })
        .then(res => res.json())
        .then(data => {
            setMessage('Livre retourné avec succès.');
            setBookId('');
            setStudentId('');
        })
        .catch(err => {
            console.error(err);
            setMessage('Une erreur est survenue lors de la restitution du livre.');
        });
    };

    return (
        <div>
            <h2>Rendre un Livre</h2>
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    placeholder="ID du Livre"
                    value={bookId}
                    onChange={e => setBookId(e.target.value)}
                />
                <input
                    type="text"
                    placeholder="ID de l'Étudiant"
                    value={studentId}
                    onChange={e => setStudentId(e.target.value)}
                />
                <button type="submit">Rendre</button>
            </form>
            {message && <p>{message}</p>}
        </div>
    );
};

export default ReturnBook;
